import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { FiBook, FiPlus, FiSave, FiX, FiList, FiVideo, FiCode, FiFileText } from 'react-icons/fi';
import { courseService } from '../../services/courseService';
import Navbar from '../../components/Navbar';

const CreateCourse = () => {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [courseData, setCourseData] = useState({
        title: '',
        description: '',
        language: '',
        isPublished: false
    });
    const [currentModule, setCurrentModule] = useState({
        title: '',
        description: ''
    });
    const [modules, setModules] = useState([]);
    const [showModuleForm, setShowModuleForm] = useState(false);

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setCourseData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleModuleChange = (e) => {
        const { name, value } = e.target;
        setCurrentModule(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const addModule = () => {
        if (!currentModule.title) {
            toast.error('Module title is required');
            return;
        }

        setModules(prev => [...prev, { ...currentModule, content: [] }]);
        setCurrentModule({ title: '', description: '' });
        setShowModuleForm(false);
    };

    const removeModule = (index) => {
        setModules(prev => prev.filter((_, i) => i !== index));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);

            // Create the course first
            const { course } = await courseService.createCourse(courseData);

            // Add modules one by one
            for (const module of modules) {
                await courseService.addModule(course._id, module);
            }

            toast.success('Course created successfully');
            navigate('/admin/courses');
        } catch (error) {
            toast.error(error.response?.data?.error || 'Failed to create course');
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            <Navbar />
            <div className="container mx-auto px-4 py-8">
                <div className="max-w-4xl mx-auto">
                    {/* Header */}
                    <div className="mb-8 flex items-center space-x-4">
                        <div className="bg-blue-100 p-3 rounded-full">
                            <FiBook className="w-8 h-8 text-blue-600" />
                        </div>
                        <div>
                            <h1 className="text-3xl font-bold text-gray-900">Create New Course</h1>
                            <p className="text-gray-600">Add a new course with modules and content</p>
                        </div>
                    </div>

                    {/* Course Form */}
                    <form onSubmit={handleSubmit} className="space-y-8">
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <h2 className="text-xl font-semibold mb-4">Course Details</h2>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                        Course Title
                                    </label>
                                    <input
                                        type="text"
                                        name="title"
                                        value={courseData.title}
                                        onChange={handleInputChange}
                                        className="w-full p-2 border rounded-md"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                        Description
                                    </label>
                                    <textarea
                                        name="description"
                                        value={courseData.description}
                                        onChange={handleInputChange}
                                        className="w-full p-2 border rounded-md"
                                        rows="4"
                                        required
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">
                                        Programming Language
                                    </label>
                                    <input
                                        type="text"
                                        name="language"
                                        value={courseData.language}
                                        onChange={handleInputChange}
                                        className="w-full p-2 border rounded-md"
                                        required
                                    />
                                </div>

                                <div className="flex items-center space-x-2">
                                    <input
                                        type="checkbox"
                                        name="isPublished"
                                        id="isPublished"
                                        checked={courseData.isPublished}
                                        onChange={handleInputChange}
                                        className="rounded text-blue-600"
                                    />
                                    <label htmlFor="isPublished" className="text-sm font-medium text-gray-700">
                                        Publish course immediately
                                    </label>
                                </div>
                            </div>
                        </div>

                        {/* Modules Section */}
                        <div className="bg-white rounded-xl shadow-lg p-6">
                            <div className="flex justify-between items-center mb-4">
                                <h2 className="text-xl font-semibold">Course Modules</h2>
                                <button
                                    type="button"
                                    onClick={() => setShowModuleForm(true)}
                                    className="flex items-center space-x-2 px-4 py-2 bg-green-100 text-green-600 rounded-lg hover:bg-green-200 transition-colors"
                                >
                                    <FiPlus className="w-5 h-5" />
                                    <span>Add Module</span>
                                </button>
                            </div>

                            {/* Module Form */}
                            {showModuleForm && (
                                <div className="mb-6 p-4 border rounded-lg bg-gray-50">
                                    <h3 className="text-lg font-medium mb-3">New Module</h3>
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                                Module Title
                                            </label>
                                            <input
                                                type="text"
                                                name="title"
                                                value={currentModule.title}
                                                onChange={handleModuleChange}
                                                className="w-full p-2 border rounded-md"
                                            />
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                                Module Description
                                            </label>
                                            <textarea
                                                name="description"
                                                value={currentModule.description}
                                                onChange={handleModuleChange}
                                                className="w-full p-2 border rounded-md"
                                                rows="3"
                                            />
                                        </div>

                                        <div className="flex justify-end space-x-3">
                                            <button
                                                type="button"
                                                onClick={() => setShowModuleForm(false)}
                                                className="px-4 py-2 text-gray-600 hover:text-gray-800"
                                            >
                                                Cancel
                                            </button>
                                            <button
                                                type="button"
                                                onClick={addModule}
                                                className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                                            >
                                                <FiPlus className="w-4 h-4" />
                                                <span>Add Module</span>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Modules List */}
                            <div className="space-y-4">
                                {modules.map((module, index) => (
                                    <div
                                        key={index}
                                        className="flex items-start justify-between p-4 border rounded-lg hover:bg-gray-50"
                                    >
                                        <div>
                                            <h3 className="font-medium">{module.title}</h3>
                                            <p className="text-sm text-gray-600">{module.description}</p>
                                        </div>
                                        <button
                                            type="button"
                                            onClick={() => removeModule(index)}
                                            className="text-red-500 hover:text-red-700"
                                        >
                                            <FiX className="w-5 h-5" />
                                        </button>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Submit Button */}
                        <div className="flex justify-end">
                            <button
                                type="submit"
                                disabled={loading}
                                className={`flex items-center space-x-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors ${
                                    loading ? 'opacity-50 cursor-not-allowed' : ''
                                }`}
                            >
                                {loading ? (
                                    <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                                ) : (
                                    <FiSave className="w-5 h-5" />
                                )}
                                <span>{loading ? 'Creating...' : 'Create Course'}</span>
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </>
    );
};

export default CreateCourse;
