import api from './api';

export const enrollmentService = {
    enrollInCourse: async (courseId) => {
        const response = await api.post(`/api/enrollments/${courseId}`);
        return response.data;
    },

    getEnrolledCourses: async () => {
        const response = await api.get('/api/enrollments/my-courses');
        return response.data;
    },

    checkEnrollmentStatus: async (courseId) => {
        const response = await api.get(`/api/enrollments/${courseId}/status`);
        return response.data;
    },

    updateEnrollmentStatus: async (courseId, status) => {
        const response = await api.put(`/api/enrollments/${courseId}/status`, { status });
        return response.data;
    }
};
