import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FiBook, FiChevronRight, FiVideo, FiFileText, FiCode } from 'react-icons/fi';
import { courseService } from '../services/courseService';
import { enrollmentService } from '../services/enrollmentService';
import Navbar from '../components/Navbar';

const CourseContent = () => {
    const { courseId } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [selectedModule, setSelectedModule] = useState(null);
    const [selectedTopic, setSelectedTopic] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        checkEnrollmentAndFetchCourse();
    }, [courseId]);

    const checkEnrollmentAndFetchCourse = async () => {
        try {
            // Check if user is enrolled
            const statusRes = await enrollmentService.checkEnrollmentStatus(courseId);
            if (!statusRes.isEnrolled) {
                navigate(`/courses/${courseId}`);
                return;
            }

            // Fetch course content
            const courseRes = await courseService.getCourse(courseId);
            setCourse(courseRes.course);

            // Select first module and topic by default
            if (courseRes.course.modules?.length > 0) {
                setSelectedModule(courseRes.course.modules[0]);
                if (courseRes.course.modules[0].mainTopics?.length > 0) {
                    setSelectedTopic(courseRes.course.modules[0].mainTopics[0]);
                }
            }
        } catch (error) {
            console.error('Error fetching course content:', error);
        } finally {
            setLoading(false);
        }
    };

    const getContentIcon = (type) => {
        switch (type) {
            case 'youtube':
                return <FiVideo className="w-5 h-5" />;
            case 'code':
                return <FiCode className="w-5 h-5" />;
            default:
                return <FiFileText className="w-5 h-5" />;
        }
    };

    const renderContent = (content) => {
        switch (content.type) {
            case 'youtube':
                const videoId = content.content.split('v=')[1];
                return (
                    <div className="aspect-w-16 aspect-h-9">
                        <iframe
                            src={`https://www.youtube.com/embed/${videoId}`}
                            title="YouTube video player"
                            frameBorder="0"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                            className="w-full h-full"
                        ></iframe>
                    </div>
                );
            case 'code':
                return (
                    <pre className="bg-gray-100 p-4 rounded-lg overflow-x-auto">
                        <code>{content.content}</code>
                    </pre>
                );
            case 'points':
                return (
                    <ul className="list-disc pl-5 space-y-2">
                        {content.content.split('\n').map((point, index) => (
                            <li key={index} className="text-gray-700">{point}</li>
                        ))}
                    </ul>
                );
            case 'heading':
                return <h2 className="text-2xl font-bold mb-4">{content.content}</h2>;
            case 'subheading':
                return <h3 className="text-xl font-semibold mb-3">{content.content}</h3>;
            default:
                return <p className="text-gray-700 mb-4">{content.content}</p>;
        }
    };

    if (loading) {
        return (
            <>
                <Navbar />
                <div className="container mx-auto px-4 py-8">
                    <div>Loading...</div>
                </div>
            </>
        );
    }

    if (!course) {
        return (
            <>
                <Navbar />
                <div className="container mx-auto px-4 py-8">
                    <div>Course not found</div>
                </div>
            </>
        );
    }

    return (
        <>
            <Navbar />
            <div className="container mx-auto px-4 py-8">
                <div className="flex flex-col lg:flex-row gap-8">
                    {/* Sidebar - Module List */}
                    <div className="lg:w-1/4">
                        <div className="bg-white rounded-lg shadow p-4">
                            <h2 className="text-xl font-bold mb-4">Course Content</h2>
                            <div className="space-y-2">
                                {course.modules?.map((module) => (
                                    <div key={module._id}>
                                        <button
                                            onClick={() => {
                                                setSelectedModule(module);
                                                if (module.mainTopics?.length > 0) {
                                                    setSelectedTopic(module.mainTopics[0]);
                                                }
                                            }}
                                            className={`w-full text-left p-3 rounded-lg flex items-center justify-between ${
                                                selectedModule?._id === module._id
                                                    ? 'bg-blue-50 text-blue-600'
                                                    : 'hover:bg-gray-50'
                                            }`}
                                        >
                                            <span className="font-medium">{module.name}</span>
                                            <FiChevronRight className={`transform transition-transform ${
                                                selectedModule?._id === module._id ? 'rotate-90' : ''
                                            }`} />
                                        </button>

                                        {selectedModule?._id === module._id && (
                                            <div className="ml-4 mt-2 space-y-1">
                                                {module.mainTopics?.map((topic) => (
                                                    <button
                                                        key={topic._id}
                                                        onClick={() => setSelectedTopic(topic)}
                                                        className={`w-full text-left p-2 rounded ${
                                                            selectedTopic?._id === topic._id
                                                                ? 'bg-blue-100 text-blue-600'
                                                                : 'hover:bg-gray-50'
                                                        }`}
                                                    >
                                                        {topic.name}
                                                    </button>
                                                ))}
                                            </div>
                                        )}
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>

                    {/* Main Content Area */}
                    <div className="lg:w-3/4">
                        <div className="bg-white rounded-lg shadow p-6">
                            {selectedTopic ? (
                                <>
                                    <h1 className="text-2xl font-bold mb-2">{selectedTopic.name}</h1>
                                    {selectedTopic.description && (
                                        <p className="text-gray-600 mb-6">{selectedTopic.description}</p>
                                    )}
                                    <div className="space-y-6">
                                        {selectedTopic.contents?.map((content, index) => (
                                            <div key={index} className="content-item">
                                                {renderContent(content)}
                                            </div>
                                        ))}
                                    </div>
                                </>
                            ) : (
                                <div className="text-center text-gray-500">
                                    <FiBook className="w-16 h-16 mx-auto mb-4" />
                                    <p>Select a topic to start learning</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default CourseContent;
